import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import styled from "styled-components";
import Button from "../ui/Button";

function Hero() {
    const [movies, setMovies] = useState('');

    const API_KEY = process.env.REACT_APP_API_KEY;

    const genres = movies && movies.genres.map((genre)=> genre.name ).join(", ");
    const idTrailer = movies && movies.videos.results[0].key


    const getTrendingMovies = async () => {
        const URL = `https://api.themoviedb.org/3/trending/all/day?api_key=${API_KEY}`
        const res = await axios(URL)

        return res.data.results[0];

    }

    async function getDetailMovie() {
        const trendingMovie = await getTrendingMovie();
        const id = trendingMovie.id;
        const URL = `https://api.themoviedb.org/3/movie/${id}?api_key=${API_KEY}&append_to_response=videos`;
        const response = await axios(URL);
        setMovie(response.data);
      }

    useEffect(getDetailedMovies, [])

    const StyledHero = styled.div`
        background-color: #2b2d42;
        color: #d5bdaf;

        section {
            padding: 3rem;
            text-align: center;
        }

        p {
            margin: 1rem 0;
        }

        img {
            border-radius: 2rem;
        }

        @media screen and (min-width: 768px) {
        section {
            max-width: 1200px;
            margin: 0 auto;
            text-align: left;
            display: flex;
            flex-direction: row;
            align-items: center;
            justify-content: space-between;
        }
}
    `

    return (
        <StyledHero>
            <section>
                <div>
                    <h2>{movies.title}</h2>
                    <h4>Genre : {genres}</h4>
                    <p>{movies.overview}</p>
                    <Button as="a" href={`https://www.youtube.com/watch?v=${idTrailer}`} target="_blank">Watch More</Button>
                </div>
                <div>
                    <img src={`https://image.tmdb.org/t/p/w500/${movies.backdrop_path}`} alt={movies.Title} />
                </div>
            </section>
        </StyledHero>
    );
}

export default Hero
